(function(){function e(){document.querySelectorAll(".chart div span").forEach(function(n){n.style.visibility="initial";n.style.width=n.getAttribute("data-width")})}function o(n){return n.toString().replace(/\B(?=(\d{3})+(?!\d))/g," ")}var t=document.querySelectorAll(".data-population-progress>tbody>tr"),n=Array.prototype.map.call(t,function(n){return{year:Number(n.children[0].innerText),population:Number(n.children[1].innerText)}}),i=n.reduce(function(n,t){return n>t.population?n:t.population},0),r=function(n){return n*330/i},u=document.querySelector(".chart"),f=n.map(function(n){return"<div>"+n.year+"<span data-width='"+r(n.population)+"px' style='width: 0;visibility: hidden;transition: width 2s ease-out'>"+o(n.population)+"<\/div>"});u.innerHTML=f.join("");setTimeout(e,500)})();